.pure-form-aligned input:not([type="radio"]):not([type="checkbox"]):not([type="button"]),
.pure-form-aligned textarea,
.pure-form-aligned select,
.pure-form-message-inline {
    display: inline-block;
    vertical-align: middle;
}
.pure-form-aligned textarea {
    vertical-align: top;
