.pure-button {
    /* Structure */
    display: inline-block;
    line-height: normal;
    white-space: nowrap;
    vertical-align: middle;
    text-align: center;
    cursor: pointer;
    -webkit-user-drag: none;
    user-select: none;
    box-sizing: border-box;
}

/* Firefox: Get rid of the inner focus border */
.pure-button::-moz-focus-inner {
    padding: 0;
    border: 0;
}

/* Inherit .pure-g styles */
.pure-button-group {
    letter-spacing: -0.31em; /* Webkit: collapse white-space between units */
    text-rendering: optimizespeed; /* Webkit: fixes text-rendering: optimizeLegibility */
}

.opera-only :-o-prefocus,
.pure-button-group {
    word-spacing: -0.43em;
}

.pure-button-group .pure-button {
    letter-spacing: normal;
    word-spacing: normal;
    vertical-align: top;
    text-rendering: auto;
}

/*csslint outline-none:false*/

.pure-button {
    font-family: inherit;
    font-size: 100%;
    padding: 0.5em 1em;
    color: rgba(0, 0, 0, 0.80);
    border: none rgba(0, 0, 0, 0);
    background-color: #E6E6E6;
    text-decoration: none;
    border-radius: 2px;
}

.pure-button-hover,
.pure-button:hover,
.pure-button:focus {
    background-image: linear-gradient(transparent, rgba(0,0,0, 0.05) 40%, rgba(0,0,0, 0.10));
}
.pure-button:focus {
    outline: 0;
}
.pure-button-active,
.pure-button:active {
    box-shadow: 0 0 0 1px rgba(0,0,0, 0.15) inset, 0 0 6px rgba(0,0,0, 0.20) inset;
    border-color: #000;
}

:host([disabled]) .pure-button,
.pure-button[disabled],
.pure-button-disabled,
.pure-button-disabled:hover,
.pure-button-disabled:focus,
.pure-button-disabled:active {
    border: none;
    background-image: none;
    opacity: 0.40;
    cursor: not-allowed;
    box-shadow: none;
    pointer-events: none;
}

.pure-button-hidden {
    display: none;
}

:host([primary]) .pure-button,
.pure-button-primary,
.pure-button-selected,
a.pure-button-primary,
a.pure-button-selected {
    background-color: rgb(0, 120, 231);
    color: #fff;
}

/* Button Groups */
.pure-button-group .pure-button {
    margin: 0;
    border-radius: 0;
    border-right: 1px solid rgba(0, 0, 0, 0.2);

}

.pure-button-group .pure-button:first-child {
    border-top-left-radius: 2px;
    border-bottom-left-radius: 2px;
}
.pure-button-group .pure-button:last-child {
    border-top-right-radius: 2px;
    border-bottom-right-radius: 2px;
    border-right: none;
}

@media only screen and (max-width : 480px) {
    button[type="submit"] {
        margin: 0.7em 0 0;
    }

    input:not([type]),
    input[type="text"],
    input[type="password"],
    input[type="email"],
    input[type="url"],
    input[type="date"],
    input[type="month"],
    input[type="time"],
    input[type="datetime"],
    input[type="datetime-local"],
    input[type="week"],
    input[type="number"],
    input[type="search"],
    input[type="tel"],
    input[type="color"],
    label {
        margin-bottom: 0.3em;
        display: block;
    }

    .pure-group input:not([type]),
    .pure-group input[type="text"],
    .pure-group input[type="password"],
    .pure-group input[type="email"],
    .pure-group input[type="url"],
    .pure-group input[type="date"],
    .pure-group input[type="month"],
    .pure-group input[type="time"],
    .pure-group input[type="datetime"],
    .pure-group input[type="datetime-local"],
    .pure-group input[type="week"],
    .pure-group input[type="number"],
    .pure-group input[type="search"],
    .pure-group input[type="tel"],
    .pure-group input[type="color"] {
        margin-bottom: 0;
    }

    .pure-form-aligned .pure-control-group label {
        margin-bottom: 0.3em;
        text-align: left;
        display: block;
        width: 100%;
    }

    .pure-form-aligned .pure-controls {
        margin: 1.5em 0 0 0;
    }

    .pure-form-message-inline,
    .pure-form-message {
        display: block;
        font-size: 0.75em;
        /* Increased bottom padding to make it group with its related input element. */
        padding: 0.2em 0 0.8em;
    }
}

:root{
    --smurf-input-box-shadow-color: #ddd;
    --smurf-input-box-shadow: inset 0 1px 3px var(--smurf-input-box-shadow-color);

    --smurf-input-border-radius: 4px;
    --smurf-input-border-color: #ccc;
    --smurf-input-border-width: 1px;
    --smurf-input-border-style: solid;
    --smurf-input-focus-border-color: #129FEA;
    --smurf-input-border: var(--smurf-input-border-width) var(--smurf-input-border-style) var(--smurf-input-border-color);

    --smurf-input-message-color: #666;

    --smurf-legend-color: #333;
    --smurf-legend-border-color: #e5e5e5;
    --smurf-legend-border-width: 1px;
    --smurf-legend-border-type: solid;
    --smurf-legend-border: var(--smurf-legend-border-width) var(--smurf-legend-border-type) var(--smurf-legend-border-color);

    --smurf-select-background-color: #fff;

    --smurf-input-disabled-background-color: #eaeded;
    --smurf-input-disabled-color: #cad2d3;

    --smurf-input-readonly-background-color: #eee; /* menu hover bg color */
    --smurf-input-readonly-color: #777; /* menu text color */
    --smurf-input-readonly-border-color: #ccc;

    --smurf-input-focus-invalid-color: #b94a48;
    --smurf-input-focus-invalid-border-color: #e9322d;
}

/*
Box-model set to false because we're setting a height on select elements, which
also have border and padding. This is done because some browsers don't render
the padding. We explicitly set the box-model for select elements to border-box,
so we can ignore the csslint warning.
*/

.pure-form input:not([type="radio"]):not([type="checkbox"]):not([type="button"]),
.pure-form select,
.pure-form textarea {
    padding: 0.5em 0.6em;
    display: inline-block;
    border: var(--smurf-input-border);
    box-shadow: var(--smurf-input-box-shadow);
    border-radius: 4px;
    vertical-align: middle;
    box-sizing: border-box;
}

.pure-form input:not([type="radio"]):not([type="checkbox"]):not([type="button"]):focus,
.pure-form select:focus,
.pure-form textarea:focus {
    outline: 0;
    border-color: var(--smurf-input-focus-border-color);
}

.pure-form input[type="file"]:focus,
.pure-form input[type="radio"]:focus,
.pure-form input[type="checkbox"]:focus {
    outline: thin var(--smurf-input-border-style) var(--smurf-input-focus-border-color);
    outline: var(--smurf-input-border-width) auto var(--smurf-input-focus-border-color);
}
.pure-checkbox,
.pure-radio{
    margin: 0.5em 0;
    display: block;
}

.pure-form input:not([type="radio"]):not([type="checkbox"]):not([type="button"])[disabled],
.pure-form select[disabled],
.pure-form textarea[disabled] {
    cursor: not-allowed;
    background-color: var(--smurf-input-disabled-background-color);
    color: var(--smurf-input-disabled-color);
}

.pure-form input:not([type="radio"]):not([type="checkbox"]):not([type="button"])[readonly],
.pure-form select[readonly],
.pure-form textarea[readonly] {
    background-color: var(--smurf-input-readonly-background-color); /* menu hover bg color */
    color: var(--smurf-input-readonly-color); /* menu text color */
    border-color: var(--smurf-input-readonly-border-color);
}

.pure-form input:not([type="radio"]):not([type="checkbox"]):not([type="button"]):focus:invalid,
.pure-form textarea:focus:invalid,
.pure-form select:focus:invalid {
    color: var(--smurf-input-focus-invalid-color);
    border-color: var(--smurf-input-focus-invalid-border-color);
}
.pure-form input[type="file"]:focus:invalid:focus,
.pure-form input[type="radio"]:focus:invalid:focus,
.pure-form input[type="checkbox"]:focus:invalid:focus {
    border-color: var(--smurf-input-focus-invalid-border-color);
}
.pure-form select {
    /* Normalizes the height; padding is not sufficient. */
    height: 2.25em;
    border: var(--smurf-input-border);
    background-color: var(--smurf-select-background-color);
}
.pure-form select[multiple] {
    height: auto;
}
.pure-form label {
    margin: 0.5em 0 0.2em;
}
.pure-form fieldset {
    margin: 0;
    padding: 0.35em 0 0.75em;
    border: 0;
}
.pure-form legend {
    display: block;
    width: 100%;
    padding: 0.3em 0;
    margin-bottom: 0.3em;
    color: var(--smurf-legend-color);
    border-bottom: var(--smurf-legend-border);
}

.pure-form-stacked input:not([type="radio"]):not([type="checkbox"]):not([type="button"]),
.pure-form-stacked select,
.pure-form-stacked label,
.pure-form-stacked textarea {
    display: block;
    margin: 0.25em 0;
}

.pure-form-aligned input:not([type="radio"]):not([type="checkbox"]):not([type="button"]),
.pure-form-aligned textarea,
.pure-form-aligned select,
.pure-form-message-inline {
    display: inline-block;
    vertical-align: middle;
}
.pure-form-aligned textarea {
    vertical-align: top;
}

/* Aligned Forms */
.pure-form-aligned .pure-control-group {
    margin-bottom: 0.5em;
}
.pure-form-aligned .pure-control-group label {
    text-align: right;
    display: inline-block;
    vertical-align: middle;
    width: 10em;
    margin: 0 1em 0 0;
}
.pure-form-aligned .pure-controls {
    margin: 1.5em 0 0 11em;
}

/* Rounded Inputs */
input:not([type="radio"]):not([type="checkbox"]):not([type="button"]).pure-input-rounded,
.pure-input-rounded {
    border-radius: 2em;
    padding: 0.5em 1em;
}

/* Grouped Inputs */
.pure-group fieldset {
    margin-bottom: 10px;
}
.pure-group input:not([type="radio"]):not([type="checkbox"]):not([type="button"]),
.pure-group textarea {
    display: block;
    padding: 10px;
    margin: 0 0 -1px;
    border-radius: 0;
    position: relative;
    top: -1px;
}
.pure-group input:not([type="radio"]):not([type="checkbox"]):not([type="button"]):focus,
.pure-group textarea:focus {
    z-index: 3;
}
.pure-group input:not([type="radio"]):not([type="checkbox"]):not([type="button"]):first-child,
.pure-group textarea:first-child {
    top: 1px;
    border-radius: 4px 4px 0 0;
    margin: 0;
}
.pure-group input:not([type="radio"]):not([type="checkbox"]):not([type="button"]):first-child:last-child,
.pure-group textarea:first-child:last-child {
    top: 1px;
    border-radius: 4px;
    margin: 0;
}
.pure-group input:not([type="radio"]):not([type="checkbox"]):not([type="button"]):last-child,
.pure-group textarea:last-child {
    top: -2px;
    border-radius: 0 0 4px 4px;
    margin: 0;
}
.pure-group button {
    margin: 0.35em 0;
}

.pure-input-1 {
    width: 100%;
}
.pure-input-3-4 {
    width: 75%;
}
.pure-input-2-3 {
    width: 66%;
}
.pure-input-1-2 {
    width: 50%;
}
.pure-input-1-3 {
    width: 33%;
}
.pure-input-1-4 {
    width: 25%;
}

/* Inline help for forms */
.pure-form-message-inline {
    display: inline-block;
    padding-left: 0.3em;
    color: var(--smurf-input-message-color);
    vertical-align: middle;
    font-size: 0.875em;
}

/* Block help for forms */
.pure-form-message {
    display: block;
    color: var(--smurf-input-message-color);
    font-size: 0.875em;
}

